export const ImageUpload = () => {
  const classes = useStyles();
  const [selectedFile, setSelectedFile] = useState();
  const [preview, setPreview] = useState();
  const [data, setData] = useState();
  const [image, setImage] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // ✅ Full disease precautions (8 classes)
  const [diseasePrecautions, setDiseasePrecautions] = useState({});

useEffect(() => {
  const fetchRemedies = async () => {
    try {
      const res = await axios.get("http://localhost:8000/remedies");
      if (res.status === 200) setDiseasePrecautions(res.data);
    } catch (error) {
      console.error("Error loading remedies.json:", error);
    }
  };
  fetchRemedies();
}, []);


  // ✅ Backend endpoint (update if different)
  const API_URL = "http://localhost:8000/predict";

  const sendFile = useCallback(async () => {
    if (image && selectedFile) {
      try {
        setIsLoading(true);
        const formData = new FormData();
        formData.append("file", selectedFile);

        const res = await axios.post(API_URL, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });

        if (res.status === 200) setData(res.data);
      } catch (error) {
        console.error("Prediction Error:", error);
        alert("Prediction failed. Check backend server or model endpoint.");
      } finally {
        setIsLoading(false);
      }
    }
  }, [image, selectedFile]);

  const clearData = () => {
    setData(null);
    setImage(false);
    setSelectedFile(null);
    setPreview(null);
  };

  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
  }, [selectedFile]);

  useEffect(() => {
    if (preview) sendFile();
  }, [preview, sendFile]);

  const onSelectFile = (files) => {
    if (!files || files.length === 0) {
      clearData();
      return;
    }
    setSelectedFile(files[0]);
    setData(undefined);
    setImage(true);
  };

  const confidence = data ? (parseFloat(data.pred_conf) * 100).toFixed(2) : 0;
  const getPrecaution = () =>
    data ? diseasePrecautions[data.pred_class] || "No precautions available." : "";

  return (
    <React.Fragment>
      <AppBar position="static" className={classes.appbar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            Tomato Disease Detection
          </Typography>
          <div className={classes.grow} />
          <Avatar src={tomatoImg}></Avatar>
        </Toolbar>
      </AppBar>

      <Container maxWidth={false} className={classes.mainContainer} disableGutters>
        <Grid
          className={classes.gridContainer}
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          spacing={2}
        >
          <Grid item xs={12}>
            <Card className={classes.imageCard}>
              {image && !isLoading && (
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={preview}
                    component="img"
                    title="Tomato leaf"
                  />
                </CardActionArea>
              )}

              {!image && !isLoading && (
                <CardContent>
                  <DropzoneArea
                    acceptedFiles={["image/*"]}
                    dropzoneText={"Drag and drop a tomato leaf image here"}
                    onChange={onSelectFile}
                  />
                </CardContent>
              )}

              {isLoading && (
                <CardContent className={classes.detail}>
                  <CircularProgress color="secondary" className={classes.loader} />
                  <Typography variant="h6">Processing...</Typography>
                </CardContent>
              )}

              {data && !isLoading && (
                <CardContent className={classes.detail}>
                  <TableContainer component={Paper} elevation={0}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell className={classes.tableCell}>Disease</TableCell>
                          <TableCell align="right" className={classes.tableCell}>
                            Confidence
                          </TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        <TableRow>
                          <TableCell className={classes.tableCell}>
                            {data.pred_class}
                          </TableCell>
                          <TableCell align="right" className={classes.tableCell}>
                            {confidence}%
                          </TableCell>
                        </TableRow>
                      </TableBody>
                    </Table>
                  </TableContainer>

                  <Typography variant="body1" className={classes.text} style={{ marginTop: "1em" }}>
                    <strong>Precautions:</strong> {getPrecaution()}
                  </Typography>
                </CardContent>
              )}
            </Card>
          </Grid>

          {data && !isLoading && (
            <Grid item className={classes.buttonGrid}>
              <ColorButton
                variant="contained"
                className={classes.clearButton}
                color="primary"
                onClick={clearData}
                startIcon={<Clear fontSize="large" />}
              >
                Clear
              </ColorButton>
            </Grid>
          )}
        </Grid>
      </Container>
    </React.Fragment>
  );
};
