import React, { useState, useEffect, useCallback } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";
import Container from "@material-ui/core/Container";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import {
  Paper,
  CardActionArea,
  CardMedia,
  Grid,
  TableContainer,
  Table,
  TableBody,
  TableHead,
  TableRow,
  TableCell,
  Button,
  CircularProgress,
} from "@material-ui/core";
import tomatoImg from "./tomato.jpg";
import bgImg from "./bg.png";
import { DropzoneArea } from "material-ui-dropzone";
import { common } from "@material-ui/core/colors";
import Clear from "@material-ui/icons/Clear";
import axios from "axios";

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(common.white),
    backgroundColor: common.white,
    "&:hover": { backgroundColor: "#ffffff7a" },
  },
}))(Button);

const useStyles = makeStyles((theme) => ({
  grow: { flexGrow: 1 },
  clearButton: {
    width: "100%",
    borderRadius: "15px",
    padding: "15px 22px",
    color: "#000000a6",
    fontSize: "20px",
    fontWeight: 900,
  },
  mainContainer: {
    backgroundImage: `url(${bgImg})`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundSize: "cover",
    height: "93vh",
    marginTop: "8px",
  },
  gridContainer: { justifyContent: "center", padding: "1em" },
  imageCard: {
    margin: "auto",
    maxWidth: 400,
    height: 500,
    backgroundColor: "transparent",
    boxShadow: "0px 9px 70px 0px rgb(0 0 0 / 30%) !important",
    borderRadius: "15px",
  },
  media: { height: 400 },
  appbar: {
    background: "#be6a77",
    boxShadow: "none",
    color: "white",
  },
  loader: { color: "#be6a77 !important" },
  tableCell: {
    fontSize: "18px",
    color: "#000000a6 !important",
    fontWeight: "bolder",
    backgroundColor: "transparent !important",
  },
  detail: {
    backgroundColor: "transparent",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  buttonGrid: { maxWidth: "416px", width: "100%" },
}));

export const ImageUpload = () => {
  const classes = useStyles();
  const [selectedFile, setSelectedFile] = useState();
  const [preview, setPreview] = useState();
  const [data, setData] = useState();
  const [image, setImage] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [remediesMap, setRemediesMap] = useState(null);

  const API_URL = "http://localhost:8000/predict";
  const REMEDIES_URL = "http://localhost:8000/remedies";

  useEffect(() => {
    let mounted = true;
    const fetchRemedies = async () => {
      try {
        const res = await axios.get(REMEDIES_URL, { timeout: 3000 });
        if (mounted && res.status === 200) setRemediesMap(res.data);
      } catch (err) {
        console.warn("Could not fetch /remedies (frontend fallback).", err?.message || err);
      }
    };
    fetchRemedies();
    return () => { mounted = false; };
  }, []);

  const sendFile = useCallback(async () => {
    if (image && selectedFile) {
      try {
        setIsLoading(true);
        const formData = new FormData();
        formData.append("file", selectedFile);
        const res = await axios.post(API_URL, formData, {
          headers: { "Content-Type": "multipart/form-data" },
          timeout: 20000,
        });

        if (res.status === 200) {
          setData(res.data);

          if ((!res.data.remedy || Object.keys(res.data.remedy).length === 0) && remediesMap) {
            const predClass =
              res.data.pred_class || res.data.predicted_class || res.data.predClass;
            if (predClass && remediesMap[predClass]) {
              setData((prev) => ({ ...prev, remedy: remediesMap[predClass] }));
            }
          }
        } else alert("Prediction failed. Check backend logs.");
      } catch (error) {
        console.error("Prediction Error:", error);
        alert("Prediction failed. Check backend server or model endpoint.");
      } finally {
        setIsLoading(false);
      }
    }
  }, [image, selectedFile, remediesMap]);

  const clearData = () => {
    setData(null);
    setImage(false);
    setSelectedFile(null);
    setPreview(null);
  };

  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
    return () => URL.revokeObjectURL(objectUrl);
  }, [selectedFile]);

  useEffect(() => {
    if (preview) sendFile();
  }, [preview, sendFile]);

  const onSelectFile = (files) => {
    if (!files || files.length === 0) {
      clearData();
      return;
    }
    setSelectedFile(files[0]);
    setData(undefined);
    setImage(true);
  };

  const getPredClass = () =>
    data?.pred_class || data?.predicted_class || data?.predClass || null;

  const getConfidenceValue = () => {
    const v =
      data?.pred_conf ??
      data?.confidence ??
      data?.pred_confidence ??
      data?.score ??
      null;
    if (v == null) return null;
    const num = Number(v);
    return Number.isNaN(num) ? null : num;
  };

  const confidenceVal = getConfidenceValue();
  const confidenceDisplay =
    confidenceVal === null ? "0.00" : (confidenceVal * 100).toFixed(2);

  // âœ… improved remedy rendering
  const renderRemedyContent = () => {
    if (!data) return null;
    const remedy = data.remedy;
    if (!remedy || Object.keys(remedy).length === 0)
      return (
        <Typography variant="body2" style={{ color: "white", marginTop: "1em" }}>
          Remedy information not available.
        </Typography>
      );

    return (
      <div style={{ marginTop: "1.5em", color: "white", textAlign: "left" }}>
        {remedy.title && <Typography variant="h6">{remedy.title}</Typography>}
        {remedy.short_description && (
          <Typography variant="body1" paragraph>
            {remedy.short_description}
          </Typography>
        )}

        {remedy.symptoms && (
          <>
            <Typography variant="subtitle1"><strong>Symptoms:</strong></Typography>
            <ul>{remedy.symptoms.map((s, i) => <li key={i}>{s}</li>)}</ul>
          </>
        )}

        {remedy.causes && (
          <>
            <Typography variant="subtitle1"><strong>Causes:</strong></Typography>
            <ul>{remedy.causes.map((c, i) => <li key={i}>{c}</li>)}</ul>
          </>
        )}

        {remedy.treatment_steps && (
          <>
            <Typography variant="subtitle1"><strong>Treatment Steps:</strong></Typography>
            <ul>
              {remedy.treatment_steps.map((step, i) => (
                <li key={i}>
                  <Typography variant="body2">
                    <strong>{step.step}. </strong>{step.action} â€” {step.details}
                  </Typography>
                </li>
              ))}
            </ul>
          </>
        )}

        {remedy.prevention && (
          <>
            <Typography variant="subtitle1"><strong>Prevention:</strong></Typography>
            <ul>{remedy.prevention.map((p, i) => <li key={i}>{p}</li>)}</ul>
          </>
        )}

        {remedy.organic_solutions && (
          <>
            <Typography variant="subtitle1"><strong>Organic Solutions:</strong></Typography>
            <ul>{remedy.organic_solutions.map((p, i) => <li key={i}>{p}</li>)}</ul>
          </>
        )}

        {remedy.chemical_solutions && (
          <>
            <Typography variant="subtitle1"><strong>Chemical Solutions:</strong></Typography>
            <ul>{remedy.chemical_solutions.map((p, i) => <li key={i}>{p}</li>)}</ul>
          </>
        )}

        {remedy.warning && (
          <Typography
            variant="body2"
            style={{ color: "#ffb3b3", marginTop: "1em" }}
          >
            âš ï¸ {remedy.warning}
          </Typography>
        )}

        {remedy.external_resources && (
          <>
            <Typography variant="subtitle1"><strong>External Resources:</strong></Typography>
            <ul>
              {remedy.external_resources.map((r, i) => (
                <li key={i}>
                  <a
                    href={r.url}
                    target="_blank"
                    rel="noreferrer"
                    style={{ color: "#9ad9ff" }}
                  >
                    {r.title}
                  </a>
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    );
  };

  return (
    <>
      <AppBar position="static" className={classes.appbar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            Tomato Disease Detection
          </Typography>
          <div className={classes.grow} />
          <Avatar src={tomatoImg}></Avatar>
        </Toolbar>
      </AppBar>

      <Container
        maxWidth={false}
        className={classes.mainContainer}
        disableGutters
      >
        <Grid
          className={classes.gridContainer}
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          spacing={2}
        >
          <Grid item xs={12}>
            <Card className={classes.imageCard}>
              {image && !isLoading && preview && (
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={preview}
                    component="img"
                    title="Tomato leaf"
                  />
                </CardActionArea>
              )}

              {!image && !isLoading && (
                <CardContent>
                  <DropzoneArea
                    acceptedFiles={["image/*"]}
                    dropzoneText={"Drag and drop a tomato leaf image here"}
                    onChange={onSelectFile}
                  />
                </CardContent>
              )}

              {isLoading && (
                <CardContent className={classes.detail}>
                  <CircularProgress color="secondary" className={classes.loader} />
                  <Typography variant="h6">Processing...</Typography>
                </CardContent>
              )}

              {data && !isLoading && (
                <CardContent className={classes.detail}>
                  <TableContainer component={Paper} elevation={0}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell className={classes.tableCell}>Disease</TableCell>
                          <TableCell align="right" className={classes.tableCell}>Confidence</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        <TableRow>
                          <TableCell className={classes.tableCell}>{getPredClass() ?? "Unknown"}</TableCell>
                          <TableCell align="right" className={classes.tableCell}>{confidenceDisplay}%</TableCell>
                        </TableRow>
                      </TableBody>
                    </Table>
                  </TableContainer>

                  {renderRemedyContent()}
                </CardContent>
              )}
            </Card>
          </Grid>

          {data && !isLoading && (
            <Grid item className={classes.buttonGrid}>
              <ColorButton
                variant="contained"
                className={classes.clearButton}
                color="primary"
                onClick={clearData}
                startIcon={<Clear fontSize="large" />}
              >
                Clear
              </ColorButton>
            </Grid>
          )}
        </Grid>
      </Container>
    </>
  );
};
