import React, { useState, useEffect, useCallback, useRef } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";
import Container from "@material-ui/core/Container";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import {
  Paper,
  CardActionArea,
  CardMedia,
  Grid,
  TableContainer,
  Table,
  TableBody,
  TableHead,
  TableRow,
  TableCell,
  Button,
  CircularProgress,
  Tooltip,
  Chip,
} from "@material-ui/core";
import tomatoImg from "./tomato.jpg";
import bgImg from "./bg.png";
import { DropzoneArea } from "material-ui-dropzone";
import { common } from "@material-ui/core/colors";
import Clear from "@material-ui/icons/Clear";
import axios from "axios";

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(common.white),
    backgroundColor: common.white,
    "&:hover": { backgroundColor: "#ffffff7a" },
  },
}))(Button);

const useStyles = makeStyles((theme) => ({
  grow: { flexGrow: 1 },
  clearButton: {
    width: "100%",
    borderRadius: "15px",
    padding: "15px 22px",
    color: "#000000a6",
    fontSize: "20px",
    fontWeight: 900,
  },
  mainContainer: {
    backgroundImage: `url(${bgImg})`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundSize: "cover",
    height: "93vh",
    marginTop: "8px",
  },
  gridContainer: { justifyContent: "center", padding: "1em" },
  imageCard: {
    margin: "auto",
    maxWidth: 440,
    height: "auto",
    backgroundColor: "transparent",
    boxShadow: "0px 9px 70px 0px rgb(0 0 0 / 30%) !important",
    borderRadius: "15px",
  },
  media: { height: 400, objectFit: "cover" },
  appbar: {
    background: "#be6a77",
    boxShadow: "none",
    color: "white",
  },
  loader: { color: "#be6a77 !important" },
  tableCell: {
    fontSize: "18px",
    color: "#000000a6 !important",
    fontWeight: "bolder",
    backgroundColor: "transparent !important",
  },
  detail: {
    backgroundColor: "rgba(149, 133, 133, 1)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  buttonGrid: { maxWidth: "456px", width: "100%" },
  remedyContent: {
    width: "100%",
    maxHeight: 420,
    overflowY: "auto",
  },
  uploadHeader: {
    textAlign: "center",
    marginBottom: theme.spacing(2),
    color: "#6b3540",
    fontWeight: 600,
  },
  tipsRow: {
    display: "flex",
    justifyContent: "center",
    gap: theme.spacing(1),
    marginTop: theme.spacing(1),
  },
}));

export const ImageUpload = () => {
  const classes = useStyles();
  const remedyRef = useRef(null);
  const [selectedFile, setSelectedFile] = useState();
  const [preview, setPreview] = useState();
  const [data, setData] = useState();
  const [image, setImage] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const API_URL = "http://localhost:8000/predict";

  // ‚úÖ Upload and get prediction
  const sendFile = useCallback(async () => {
    if (image && selectedFile) {
      try {
        setIsLoading(true);
        const formData = new FormData();
        formData.append("file", selectedFile);

        const res = await axios.post(API_URL, formData, {
          headers: { "Content-Type": "multipart/form-data" },
          timeout: 20000,
        });

        if (res.status === 200) {
          setData(res.data);
          setTimeout(() => {
            if (remedyRef.current) {
              remedyRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
            }
          }, 150);
        } else {
          alert("Prediction failed. Check backend logs.");
        }
      } catch (error) {
        console.error("Prediction Error:", error);
        alert("Prediction failed. Check backend server or model endpoint.");
      } finally {
        setIsLoading(false);
      }
    }
  }, [image, selectedFile]);

  // ‚úÖ Clear results
  const clearData = () => {
    setData(null);
    setImage(false);
    setSelectedFile(null);
    setPreview(null);
  };

  // ‚úÖ Image preview
  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
    return () => URL.revokeObjectURL(objectUrl);
  }, [selectedFile]);

  // ‚úÖ Send image when ready
  useEffect(() => {
    if (preview) sendFile();
  }, [preview, sendFile]);

  const onSelectFile = (files) => {
    if (!files || files.length === 0) {
      clearData();
      return;
    }
    setSelectedFile(files[0]);
    setData(undefined);
    setImage(true);
  };

  const getPredClass = () =>
    data?.pred_class || data?.predicted_class || data?.predClass || "Unknown";

  const getConfidenceValue = () => {
    const v =
      data?.pred_conf ??
      data?.confidence ??
      data?.pred_confidence ??
      data?.score ??
      null;
    if (v == null) return 0;
    const num = Number(v);
    return Number.isNaN(num) ? 0 : num;
  };

  const confidenceDisplay = (getConfidenceValue() * 100).toFixed(2);

  // ‚úÖ Render All Remedies Sections
  const renderRemedyContent = () => {
    const remedy = data?.remedy;
    if (!remedy)
      return (
        <Typography variant="body2" style={{ color: "white", marginTop: "1em" }}>
          Remedy information not available.
        </Typography>
      );

    const renderSection = (title, content, icon = "") => {
      if (!content) return null;
      const renderList = (arr) => arr.map((item, i) => <li key={i}>{item}</li>);
      return (
        <details className="remedy-block" open>
          <summary>
            {icon} {title}
          </summary>
          {Array.isArray(content) ? (
            <ul className="remedy-list">{renderList(content)}</ul>
          ) : (
            <Typography variant="body2" style={{ color: "#fff" }}>
              {content}
            </Typography>
          )}
        </details>
      );
    };

    return (
      <div className={`remedy-section ${classes.remedyContent}`}>
        {renderSection("Symptoms", remedy.symptoms, "üåø")}
        {renderSection("Causes", remedy.causes, "üß¨")}
        {renderSection("Treatment Steps", remedy.treatment_steps, "üíä")}
        {renderSection("Prevention", remedy.prevention, "üõ°Ô∏è")}
        {renderSection("Organic Solutions", remedy.organic_solutions, "üå±")}
        {renderSection("Chemical Solutions", remedy.chemical_solutions, "üß™")}

        {remedy.estimated_recovery_time && (
          <Typography variant="body2" style={{ color: "#fff", marginTop: "1em" }}>
            ‚è±Ô∏è <strong>Estimated Recovery Time:</strong> {remedy.estimated_recovery_time}
          </Typography>
        )}

        {remedy.severity_level && (
          <Typography variant="body2" style={{ color: "#fff" }}>
            üî• <strong>Severity Level:</strong> {remedy.severity_level}
          </Typography>
        )}

        {remedy.warning && (
          <Typography variant="body2" style={{ color: "#ffb3b3", marginTop: "1em" }}>
            ‚ö†Ô∏è {remedy.warning}
          </Typography>
        )}
      </div>
    );
  };

  return (
    <>
      <AppBar position="static" className={classes.appbar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            Tomato Disease Detection
          </Typography>
          <div className={classes.grow} />
          <Avatar src={tomatoImg}></Avatar>
        </Toolbar>
      </AppBar>

      <Container maxWidth={false} className={classes.mainContainer} disableGutters>
        <Grid
          className={classes.gridContainer}
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          spacing={2}
        >
          <Grid item xs={12}>
            <Card className={classes.imageCard}>
              {/* PREVIEW */}
              {image && !isLoading && preview && (
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={preview}
                    component="img"
                    title="Tomato leaf"
                  />
                </CardActionArea>
              )}

              {/* UPLOAD */}
              {!image && !isLoading && (
                <CardContent>
                  <div className={classes.uploadHeader}>
                    <Typography variant="h6">
                      Drag & drop a tomato leaf image
                    </Typography>
                    <div className={classes.tipsRow}>
                      <Chip size="small" label="JPG/PNG" />
                      <Chip size="small" label="Max 5 MB" />
                      <Chip size="small" label="1 image only" />
                    </div>
                  </div>
                  <DropzoneArea
                    acceptedFiles={["image/*"]}
                    filesLimit={1}
                    maxFileSize={5 * 1024 * 1024}
                    showAlerts={["error"]}
                    dropzoneClass={"upload-area"}
                    dropzoneText={
                      "Drag and drop a tomato leaf image here or click to browse"
                    }
                    onChange={onSelectFile}
                  />
                  <Typography className="upload-tip" align="center">
                    Tip: Clear, well-lit photos improve accuracy.
                  </Typography>
                </CardContent>
              )}

              {/* LOADING */}
              {isLoading && (
                <CardContent className={classes.detail}>
                  <CircularProgress color="secondary" className={classes.loader} />
                  <Typography variant="h6">Processing...</Typography>
                </CardContent>
              )}

              {/* RESULT */}
              {data && !isLoading && (
                <CardContent ref={remedyRef} className={classes.detail}>
                  <TableContainer component={Paper} elevation={0}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell className={classes.tableCell}>Disease</TableCell>
                          <TableCell align="right" className={classes.tableCell}>
                            Confidence
                          </TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        <TableRow>
                          <TableCell className={classes.tableCell}>
                            <Tooltip title="Predicted class from the model">
                              <span>{getPredClass()}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell align="right" className={classes.tableCell}>
                            {confidenceDisplay}%
                          </TableCell>
                        </TableRow>
                      </TableBody>
                    </Table>
                  </TableContainer>

                  {renderRemedyContent()}
                </CardContent>
              )}
            </Card>
          </Grid>

          {data && !isLoading && (
            <Grid item className={classes.buttonGrid}>
              <ColorButton
                variant="contained"
                className={classes.clearButton}
                color="primary"
                onClick={clearData}
                startIcon={<Clear fontSize="large" />}
              >
                Clear
              </ColorButton>
            </Grid>
          )}
        </Grid>
      </Container>
    </>
  );
};
